class Url2Html{constructor(){this.elements=document.querySelectorAll("[data-url2html]")}sanitizeInput(value){return value.replace(/[^A-Za-z0-9,. -]/g,"")}getUrlParameter(name){name=name.replace(/[\[]/,"\\[").replace(/[\]]/,"\\]");const regex=new RegExp("[\\?&]"+name+"=([^&#]*)");const results=regex.exec(location.search);let paramValue=results===null?"":decodeURIComponent(results[1].replace(/\+/g," "));paramValue=this.sanitizeInput(paramValue);return paramValue}replacePlaceholders(){this.elements.forEach(element=>{const paramName=element.getAttribute("data-url2html");const paramValue=this.getUrlParameter(paramName);if(paramValue!==""){element.textContent=paramValue}})}init(){this.replacePlaceholders()}}const url2html=new Url2Html;url2html.init();